import React, { useState } from 'react';

const Toolbar = ({ setColor, setLineWidth, clearCanvas, undo }) => {
  const [isVisible, setIsVisible] = useState(false);

  return (
    <div 
      style={{ 
        width: isVisible ? '50px' : '0', // Cambia el ancho al pasar el mouse
        overflow: 'hidden', // Oculta el contenido si el ancho es 0
        padding: isVisible ? '10px' : '0', 
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        backgroundColor: '#f0f0f0',
        borderRight: '1px solid #ccc',
        height: '100vh',
        transition: 'width 0.3s, padding 0.3s' // Transici칩n suave al cambiar el tama침o
      }}
      onMouseEnter={() => setIsVisible(true)} // Muestra la barra al pasar el mouse
      onMouseLeave={() => setIsVisible(false)} // Oculta la barra al salir el mouse
    >
      <input type="color" onChange={(e) => setColor(e.target.value)} style={{ marginBottom: '10px' }} />
      <input type="range" min="1" max="20" onChange={(e) => setLineWidth(e.target.value)} style={{ marginBottom: '10px' }} />
      <button onClick={clearCanvas} style={{ marginBottom: '10px' }}>Limpiar Todo</button>
      <button onClick={undo}>Deshacer</button>
    </div>
  );
};

export default Toolbar;
