import React, { useRef, useEffect, useState } from 'react';

const Canvas = ({ currentStroke, onClearCanvas, onUndo }) => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [ctx, setCtx] = useState(null);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    
    // Ajustar el tamaño del canvas
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const background = new Image();
    background.src = '/background.jpg';
    background.onload = () => {
      context.drawImage(background, 0, 0, canvas.width, canvas.height);
    };

    setCtx(context);

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      context.drawImage(background, 0, 0, canvas.width, canvas.height);
    };
    window.addEventListener('resize', resizeCanvas);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
    };
  }, []);

  const startDrawing = (e) => {
    setIsDrawing(true);
    ctx.beginPath();
    ctx.lineWidth = currentStroke.width;
    ctx.strokeStyle = currentStroke.color;
    ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    ctx.stroke();
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    ctx.closePath();
    saveState();
  };

  const saveState = () => {
    const imageData = canvasRef.current.toDataURL();
    setHistory([...history, imageData]);
  };

  const undo = () => {
    if (history.length > 0) {
      const lastState = history.pop();
      const img = new Image();
      img.src = lastState;
      img.onload = () => ctx.drawImage(img, 0, 0);
    }
  };

  const clearCanvas = () => {
    if (!ctx) return; // Asegúrate de que el contexto esté definido
    
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    
    // Volver a dibujar la imagen de fondo
    const background = new Image();
    background.src = '/background.jpg';
    background.onload = () => {
      ctx.drawImage(background, 0, 0, canvasRef.current.width, canvasRef.current.height);
    };
  };
  

  useEffect(() => {
    if (ctx) {
      onClearCanvas(clearCanvas);
      onUndo(undo);
    }
  }, [ctx, onClearCanvas, onUndo]);
  
  return (
    <div>
      <canvas
        ref={canvasRef}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
      />
    </div>
  );
};

export default Canvas;
