import React, { useState } from 'react';
import Canvas from './components/Canvas';
import Toolbar from './components/Toolbar';
import './App.css';

function App() {
  const [currentStroke, setCurrentStroke] = useState({
    color: '#000000',
    width: 5,
    type: 'solid',
  });

  const [clearCanvasFn, setClearCanvasFn] = useState(null);
  const [undoFn, setUndoFn] = useState(null);

  const changeStrokeSettings = (settings) => {
    console.log('Configuración de trazo actualizada:', settings);
    setCurrentStroke(settings);
  };

  return (
    <div className="app-container">
      <Toolbar
        currentStroke={currentStroke}
        onChangeStroke={changeStrokeSettings}
        clearCanvas={clearCanvasFn}
        undo={undoFn}
      />
      <Canvas
        currentStroke={currentStroke}
        onClearCanvas={setClearCanvasFn}
        onUndo={setUndoFn}
      />
    </div>
  );
}

export default App;
