import React from 'react';

const Toolbar = ({ currentStroke, onChangeStroke }) => {
  const changeColor = (e) => {
    onChangeStroke({ ...currentStroke, color: e.target.value });
  };

  const changeWidth = (e) => {
    onChangeStroke({ ...currentStroke, width: e.target.value });
  };

  const changeStrokeType = (type) => {
    onChangeStroke({ ...currentStroke, type });
  };

  return (
    <div className="toolbar">
      <label>Color:</label>
      <input type="color" value={currentStroke.color} onChange={changeColor} />
      <label>Grosor:</label>
      <input type="range" min="1" max="50" value={currentStroke.width} onChange={changeWidth} />
      <button onClick={() => changeStrokeType('solid')}>Trazo SÃ³lido</button>
      <button onClick={() => changeStrokeType('spray')}>Grafiti</button>
    </div>
  );
};

export default Toolbar;
