import React from 'react';

const Toolbar = ({ setColor, setLineWidth, clearCanvas, undo }) => {
  return (
    <div style={{ textAlign: 'center', marginBottom: '10px' }}>
      <input type="color" onChange={(e) => setColor(e.target.value)} />
      <input type="range" min="1" max="20" onChange={(e) => setLineWidth(e.target.value)} />
      <button onClick={clearCanvas}>Limpiar Todo</button>
      <button onClick={undo}>Deshacer</button>
    </div>
  );
};

export default Toolbar;
