import React, { useState } from 'react';
import Canvas from './components/Canvas';
import Toolbar from './components/Toolbar';
import './App.css';

function App() {
  const [currentStroke, setCurrentStroke] = useState({
    color: '#000000',
    width: 5,
    type: 'solid',
  });

  const changeStrokeSettings = (settings) => {
    setCurrentStroke(settings);
  };

  return (
    <div className="app-container">
      <h1>Paint App</h1>
      <Toolbar currentStroke={currentStroke} onChangeStroke={changeStrokeSettings} />
      <Canvas currentStroke={currentStroke} />
    </div>
  );
}

export default App;
