import React from 'react';

const Toolbar = ({ currentStroke, onChangeStroke, clearCanvas, undo }) => {
  const changeColor = (e) => {
    onChangeStroke({ ...currentStroke, color: e.target.value });
  };

  const changeWidth = (e) => {
    onChangeStroke({ ...currentStroke, width: e.target.value });
  };

  const changeStrokeType = (type) => {
    onChangeStroke({ ...currentStroke, type });
  };

  const handleClearCanvas = () => {
    if (clearCanvas) clearCanvas();
  };

  const handleUndo = () => {
    if (undo) undo();
  };

  return (
    <div className="toolbar">
      <div className="toolbar-item">
        <label htmlFor="color-picker">Color</label>
        <input id="color-picker" type="color" value={currentStroke.color} onChange={changeColor} />
      </div>

      <div className="toolbar-item">
        <label htmlFor="brush-size">Grosor</label>
        <input
          id="brush-size"
          type="range"
          min="1"
          max="50"
          value={currentStroke.width}
          onChange={changeWidth}
        />
      </div>

      <div className="toolbar-item">
        <button onClick={() => changeStrokeType('solid')}>Pincel</button>
      </div>

      <div className="toolbar-item">
        <button onClick={() => changeStrokeType('spray')}>Spray</button>
      </div>

      <div className="toolbar-item">
        <button onClick={handleClearCanvas}>Limpiar Todo</button>
      </div>

      <div className="toolbar-item">
        <button onClick={handleUndo}>Deshacer</button>
      </div>
    </div>
  );
};

export default Toolbar;
