import React, { useRef, useState } from 'react';
import Canvas from './components/Canvas';
import Toolbar from './';
import './index.css'; // Asegúrate de que este archivo se importe

const App = () => {
  const canvasRef = useRef(null);
  const [color, setColor] = useState('#000000');
  const [lineWidth, setLineWidth] = useState(5);
  const [drawnImage, setDrawnImage] = useState(null); // Para deshacer

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    const img = new Image();
    img.src = '/path/to/background/image.jpg'; // Cargar la imagen de fondo
    img.onload = () => {
      context.drawImage(img, 0, 0, canvas.width, canvas.height);
    };
  };

  const undo = () => {
    // Lógica para deshacer (puedes implementarla aquí)
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <Toolbar setColor={setColor} setLineWidth={setLineWidth} clearCanvas={clearCanvas} undo={undo} />
      <Canvas ref={canvasRef} color={color} lineWidth={lineWidth} />
    </div>
  );
};

export default App;
