import React, { useRef, useState } from 'react';
import Toolbar from './components/Toolbar';

const App = () => {
  const canvasRef = useRef(null);
  const [drawingHistory, setDrawingHistory] = useState([]);
  const [isDrawing, setIsDrawing] = useState(false);
  const [lastX, setLastX] = useState(0);
  const [lastY, setLastY] = useState(0);
  const [color, setColor] = useState('#000000');
  const [lineWidth, setLineWidth] = useState(5);

  const startDrawing = (e) => {
    setIsDrawing(true);
    setLastX(e.clientX);
    setLastY(e.clientY);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    context.strokeStyle = color;
    context.lineWidth = lineWidth;

    context.beginPath();
    context.moveTo(lastX, lastY);
    context.lineTo(e.clientX, e.clientY);
    context.stroke();

    setLastX(e.clientX);
    setLastY(e.clientY);

    // Guardar en el historial
    const currentDrawing = {
      startX: lastX,
      startY: lastY,
      endX: e.clientX,
      endY: e.clientY,
      color: color,
      lineWidth: lineWidth,
    };
    setDrawingHistory([...drawingHistory, currentDrawing]);
  };

  const stopDrawing = () => {
    setIsDrawing(false);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    setDrawingHistory([]); // Limpia el historial al limpiar el lienzo
  };

  const undo = () => {
    if (drawingHistory.length > 0) {
      const newHistory = drawingHistory.slice(0, -1); // Elimina el último trazo
      setDrawingHistory(newHistory);

      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');
      context.clearRect(0, 0, canvas.width, canvas.height); // Limpia el lienzo

      // Redibujar el historial restante
      newHistory.forEach((line) => {
        context.beginPath();
        context.moveTo(line.startX, line.startY);
        context.lineTo(line.endX, line.endY);
        context.strokeStyle = line.color;
        context.lineWidth = line.lineWidth;
        context.stroke();
      });
    }
  };

  return (
    <div>
      <Toolbar 
        setColor={setColor} 
        setLineWidth={setLineWidth} 
        clearCanvas={clearCanvas} 
        undo={undo} 
      />
      <canvas
        ref={canvasRef}
        width={window.innerWidth}
        height={window.innerHeight}
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
      />
    </div>
  );
};

export default App;
