import React, { useRef, useEffect } from 'react';

const Canvas = ({ currentStroke, onClearCanvas, onUndo }) => {
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const drawHistory = useRef([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctxRef.current = ctx;

    const background = new Image();
    background.src = '/background.jpg'; // Ruta de la imagen de fondo
    background.onload = () => {
      ctx.drawImage(background, 0, 0, canvas.width, canvas.height);
    };

    onClearCanvas(() => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      // Redibujar la imagen de fondo después de limpiar el canvas
      ctx.drawImage(background, 0, 0, canvas.width, canvas.height);
    });

    onUndo(() => {
      if (drawHistory.current.length > 0) {
        drawHistory.current.pop();
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        // Redibujar la imagen de fondo después de deshacer
        ctx.drawImage(background, 0, 0, canvas.width, canvas.height);
        drawHistory.current.forEach((drawAction) => drawAction());
      }
    });
  }, [onClearCanvas, onUndo]);

  return <canvas ref={canvasRef} width={800} height={600} />;
};

export default Canvas;
