import React, { useState } from 'react';

const Toolbar = ({ setColor, setLineWidth, clearCanvas, undo }) => {
  const [isVisible, setIsVisible] = useState(false);

  return (
    <div 
      style={{ 
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        height: '100vh',
        backgroundColor: '#f0f0f0',
        borderRight: '1px solid #ccc',
        position: 'relative', // Para que la barra pueda superponerse al lienzo
        zIndex: 1,
      }}
    >
      {/* Área invisible que activa la barra */}
      <div 
        style={{
          position: 'absolute',
          left: '0',
          top: '0',
          width: '30px', // Cambia este valor para ajustar el área de activación
          height: '100%',
          zIndex: 0, // Asegúrate de que esté detrás de la barra
        }}
        onMouseEnter={() => setIsVisible(true)}
        onMouseLeave={() => setIsVisible(false)}
      />
      
      <div 
        style={{ 
          width: isVisible ? '50px' : '0', 
          overflow: 'hidden', 
          padding: isVisible ? '10px' : '0', 
          transition: 'width 0.3s, padding 0.3s',
        }}
      >
        <input type="color" onChange={(e) => setColor(e.target.value)} style={{ marginBottom: '10px' }} />
        <input type="range" min="1" max="20" onChange={(e) => setLineWidth(e.target.value)} style={{ marginBottom: '10px' }} />
        <button onClick={clearCanvas} style={{ marginBottom: '10px' }}>Limpiar Todo</button>
        <button onClick={undo}>Deshacer</button>
      </div>
    </div>
  );
};

export default Toolbar;
