import React, { useRef, useState } from 'react';
import Canvas from './components/Canvas';
import Toolbar from './components/Toolbar';
import './index.css'; // Asegúrate de que este archivo se importe

const App = () => {
  const canvasRef = useRef(null);
  const [color, setColor] = useState('#000000');
  const [lineWidth, setLineWidth] = useState(5);
  const [history, setHistory] = useState([]); // Para almacenar el historial de dibujos
  const [isDrawing, setIsDrawing] = useState(false); // Estado para verificar si se está dibujando

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    loadBackground(); // Cargar la imagen de fondo
  };

  const loadBackground = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const img = new Image();
    img.src = '/path/to/background/image.jpg'; // Ruta de la imagen de fondo
    img.onload = () => {
      context.drawImage(img, 0, 0, canvas.width, canvas.height);
    };
  };

  const startDrawing = (event) => {
    setIsDrawing(true);
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Captura el estado actual del lienzo
    const dataUrl = canvas.toDataURL();
    if (history.length >= 5) {
      history.shift(); // Elimina el primer elemento si hay más de 5
    }
    setHistory((prev) => [...prev, dataUrl]); // Agrega el estado a la pila

    const rect = canvas.getBoundingClientRect(); // Obtiene el rectángulo del lienzo
    const x = event.clientX - rect.left; // Calcula la posición X relativa al lienzo
    const y = event.clientY - rect.top; // Calcula la posición Y relativa al lienzo

    context.lineWidth = lineWidth;
    context.strokeStyle = color;
    context.beginPath();
    context.moveTo(x, y); // Usa las coordenadas ajustadas
  };

  const draw = (event) => {
    if (!isDrawing) return;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    const rect = canvas.getBoundingClientRect(); // Obtiene el rectángulo del lienzo
    const x = event.clientX - rect.left; // Calcula la posición X relativa al lienzo
    const y = event.clientY - rect.top; // Calcula la posición Y relativa al lienzo

    context.lineTo(x, y); // Usa las coordenadas ajustadas
    context.stroke();
  };

  const stopDrawing = () => {
    setIsDrawing(false);
  };

  const undo = () => {
    if (history.length > 0) {
      const newHistory = [...history];
      newHistory.pop(); // Elimina el último estado de la pila

      const previousDrawing = newHistory[newHistory.length - 1]; // Obtiene el nuevo estado más reciente

      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');

      // Limpia el lienzo
      context.clearRect(0, 0, canvas.width, canvas.height);
      loadBackground(); // Recarga la imagen de fondo

      if (previousDrawing) {
        // Dibuja el estado anterior en el lienzo
        const img = new Image();
        img.src = previousDrawing;
        img.onload = () => {
          context.drawImage(img, 0, 0);
        };
      }

      setHistory(newHistory); // Actualiza la pila de historial
    }
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <Toolbar setColor={setColor} setLineWidth={setLineWidth} clearCanvas={clearCanvas} undo={undo} />
      <Canvas
        ref={canvasRef}
        color={color}
        lineWidth={lineWidth}
        startDrawing={startDrawing}
        draw={draw}
        stopDrawing={stopDrawing}
      />
    </div>
  );
};

export default App;
