import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBrush, faUndo, faEraser, faSprayCan, faPalette } from '@fortawesome/free-solid-svg-icons';

const Toolbar = ({ currentStroke, onChangeStroke, clearCanvas, undo }) => {
  const changeColor = (e) => {
    console.log('Color cambiado a:', e.target.value);
    onChangeStroke({ ...currentStroke, color: e.target.value });
  };

  const changeWidth = (e) => {
    console.log('Grosor cambiado a:', e.target.value);
    onChangeStroke({ ...currentStroke, width: e.target.value });
  };

  const changeStrokeType = (type) => {
    console.log('Tipo de trazo cambiado a:', type);
    onChangeStroke({ ...currentStroke, type });
  };

  const handleClearCanvas = () => {
    console.log('Limpiar lienzo');
    if (clearCanvas) clearCanvas();
  };

  const handleUndo = () => {
    console.log('Deshacer');
    if (undo) undo();
  };

  return (
    <div className="toolbar">
      <div className="toolbar-item">
        <label htmlFor="color-picker">
          <FontAwesomeIcon icon={faPalette} />
        </label>
        <input id="color-picker" type="color" value={currentStroke.color} onChange={changeColor} />
      </div>

      <div className="toolbar-item">
        <label htmlFor="brush-size">
          <FontAwesomeIcon icon={faBrush} />
        </label>
        <input
          id="brush-size"
          type="range"
          min="1"
          max="50"
          value={currentStroke.width}
          onChange={changeWidth}
        />
      </div>

      <div className="toolbar-item">
        <button onClick={() => changeStrokeType('solid')}>
          <FontAwesomeIcon icon={faBrush} />
        </button>
      </div>

      <div className="toolbar-item">
        <button onClick={() => changeStrokeType('spray')}>
          <FontAwesomeIcon icon={faSprayCan} />
        </button>
      </div>

      <div className="toolbar-item">
        <button onClick={handleClearCanvas}>
          <FontAwesomeIcon icon={faEraser} />
        </button>
      </div>

      <div className="toolbar-item">
        <button onClick={handleUndo}>
          <FontAwesomeIcon icon={faUndo} />
        </button>
      </div>
    </div>
  );
};

export default Toolbar;
