import React, { useState } from 'react';
import 'font'; // Asegúrate de importar Font Awesome

const Toolbar = ({ setColor, setLineWidth, clearCanvas, undo }) => {
  const [isVisible, setIsVisible] = useState(false);

  const handleMouseEnter = () => {
    setIsVisible(true);
  };

  const handleMouseLeave = () => {
    setIsVisible(false);
  };

  return (
    <div 
      style={{ 
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        height: '100vh',
        backgroundColor: '#ffffff', // Cambia a un color de fondo más estético
        borderRight: '1px solid #ccc',
        position: 'relative',
        zIndex: 1,
        boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)', // Sombra para darle profundidad
      }}
    >
      <div 
        style={{
          position: 'absolute',
          left: '0',
          top: '0',
          width: '30px',
          height: '100%',
          zIndex: 0,
        }}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      />
      
      <div 
        style={{ 
          width: isVisible ? '60px' : '0', // Ajusta el ancho según los iconos
          overflow: 'hidden', 
          padding: isVisible ? '10px' : '0', 
          transition: 'width 0.3s, padding 0.3s',
        }}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      >
        {/* Color Picker con Icono */}
        <div style={{ marginBottom: '10px', textAlign: 'center' }}>
          <input type="color" onChange={(e) => setColor(e.target.value)} style={{ border: 'none', width: '30px', height: '30px' }} />
          <span style={{ display: 'block', marginTop: '5px' }} className="fa fa-paint-brush" />
        </div>

        {/* Grosor con Icono */}
        <div style={{ marginBottom: '10px', textAlign: 'center' }}>
          <input type="range" min="1" max="20" onChange={(e) => setLineWidth(e.target.value)} style={{ width: '100%' }} />
          <span style={{ display: 'block', marginTop: '5px' }} className="fa fa-pencil" />
        </div>

        {/* Botón de Limpiar con Icono */}
        <div style={{ marginBottom: '10px', textAlign: 'center' }}>
          <button onClick={clearCanvas} style={buttonStyle}>
            <i className="fa fa-eraser" />
          </button>
        </div>

        {/* Botón de Deshacer con Icono */}
        <div style={{ textAlign: 'center' }}>
          <button onClick={undo} style={buttonStyle}>
            <i className="fa fa-undo" />
          </button>
        </div>
      </div>
    </div>
  );
};

// Estilo para los botones
const buttonStyle = {
  background: 'none',
  border: 'none',
  cursor: 'pointer',
  fontSize: '20px', // Tamaño del icono
  color: '#333', // Color del icono
  transition: 'color 0.3s',
};

export default Toolbar;
