import React, { useEffect, forwardRef } from 'react';

const Canvas = forwardRef(({ color, lineWidth, strokeStyle }, ref) => {
  useEffect(() => {
    const canvas = ref.current;
    const context = canvas.getContext('2d');
    context.lineWidth = lineWidth;
    context.strokeStyle = color;
    context.lineCap = strokeStyle;

    const img = new Image();
    img.src = '/path/to/background/image.jpg'; // Reemplaza con la ruta de tu imagen
    img.onload = () => {
      context.drawImage(img, 0, 0, canvas.width, canvas.height);
    };

    const draw = (event) => {
      context.lineTo(event.clientX - canvas.offsetLeft, event.clientY - canvas.offsetTop);
      context.stroke();
    };

    canvas.addEventListener('mousedown', (event) => {
      context.beginPath();
      context.moveTo(event.clientX - canvas.offsetLeft, event.clientY - canvas.offsetTop);
      canvas.addEventListener('mousemove', draw);
    });

    canvas.addEventListener('mouseup', () => {
      canvas.removeEventListener('mousemove', draw);
      context.closePath();
    });

    return () => {
      canvas.removeEventListener('mousemove', draw);
    };
  }, [color, lineWidth, strokeStyle, ref]);

  return (
    <canvas 
      ref={ref} 
      width={window.innerWidth} 
      height={window.innerHeight} 
      style={{ 
        border: '1px solid black', 
        display: 'block', 
        backgroundImage: 'url(/path/to/background/image.jpg)', // Fondo del lienzo
        backgroundSize: 'cover'
      }} 
    />
  );
});

export default Canvas;
