import React, { useState, useRef, useEffect } from 'react';
import Canvas from './components/Canvas';
import Toolbar from './components/Toolbar';
import './App.css';

function App() {
  const [currentStroke, setCurrentStroke] = useState({
    color: '#000000',
    width: 5,
    type: 'solid',
  });

  const canvasRef = useRef(null);

  // Función para actualizar la configuración del trazo
  const changeStrokeSettings = (settings) => {
    setCurrentStroke(settings);
  };

  // Función para limpiar el lienzo
  const clearCanvas = () => {
    if (canvasRef.current) {
      canvasRef.current.clearCanvas();
    }
  };

  // Función para deshacer el último trazo
  const undo = () => {
    if (canvasRef.current) {
      canvasRef.current.undo();
    }
  };

  return (
    <div className="app-container">
      <Toolbar
        currentStroke={currentStroke}
        onChangeStroke={changeStrokeSettings}
        clearCanvas={clearCanvas}
        undo={undo}
      />
      <Canvas ref={canvasRef} currentStroke={currentStroke} />
    </div>
  );
}

export default App;
