// En tu App.js
import React, { useRef, useState } from 'react';
import Toolbar from './components/Toolbar';

const App = () => {
  const [color, setColor] = useState('#000000');
  const [lineWidth, setLineWidth] = useState(5);
  const canvasRef = useRef(null);
  const [drawingHistory, setDrawingHistory] = useState([]);

  // Implementa la lógica para limpiar el lienzo
  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
  };

  // Implementa la lógica para deshacer
  const undo = () => {
    if (drawingHistory.length > 0) {
      const lastDrawing = drawingHistory[drawingHistory.length - 1];
      setDrawingHistory(drawingHistory.slice(0, -1));
      const canvas = canvasRef.current;
      const context = canvas.getContext('2d');
      // Aquí deberías redibujar el último estado guardado en el historial
      context.clearRect(0, 0, canvas.width, canvas.height);
      lastDrawing.forEach((line) => {
        context.beginPath();
        context.moveTo(line.startX, line.startY);
        context.lineTo(line.endX, line.endY);
        context.strokeStyle = line.color;
        context.lineWidth = line.lineWidth;
        context.stroke();
      });
    }
  };

  // Tu lógica para dibujar en el lienzo...

  return (
    <div>
      <Toolbar 
        setColor={setColor} 
        setLineWidth={setLineWidth} 
        clearCanvas={clearCanvas} 
        undo={undo} 
      />
      <canvas ref={canvasRef} width={window.innerWidth} height={window.innerHeight} />
    </div>
  );
};

export default App;
